import React, { useState } from 'react';
import Question from './Question';
import Result from './Result';

function App() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [selectedBreed, setSelectedBreed] = useState(null);

  const questions = [
    { question: "ËÄ≥„ÅØÁ´ã„Å£„Å¶„ÅÑ„Åæ„Åô„ÅãÔºü", options: ["„ÅØ„ÅÑ", "„ÅÑ„ÅÑ„Åà"] },
    { question: "‰Ωì„ÅÆÂ§ß„Åç„Åï„ÅØÔºü", options: ["Â∞è„Åï„ÅÑ", "‰∏≠„Åè„Çâ„ÅÑ", "Â§ß„Åç„ÅÑ"] },
    { question: "„Åó„Å£„ÅΩ„ÅÆÈï∑„Åï„ÅØÔºü", options: ["Áü≠„ÇÅ", "Èï∑„ÇÅ"] },
    { question: "Ë∂≥„ÅÆÈï∑„Åï„ÅØÔºü", options: ["Áü≠„ÇÅ", "Èï∑„ÇÅ"] },
  ];

  // Ëã±Ë™û ‚Üí Êó•Êú¨Ë™û„ÅÆÂ§âÊèõË°®
  const breedMapping = {
    shiba: "Êü¥Áä¨",
    "retriever/golden": "„Ç¥„Éº„É´„Éá„É≥„Éª„É¨„Éà„É™„Éê„Éº",
    beagle: "„Éì„Éº„Ç∞„É´"
  };

  // ÂõûÁ≠î„ÅÆÁµÑ„ÅøÂêà„Çè„Åõ„ÅßÁä¨Á®Æ„ÇíÁâπÂÆö
  const determineBreed = (answers) => {
    if (answers[0] === "„ÅØ„ÅÑ" && answers[1] === "Â∞è„Åï„ÅÑ") {
      return { en: "shiba", jp: breedMapping["shiba"] }; // Êü¥Áä¨
    } else if (answers[0] === "„ÅÑ„ÅÑ„Åà" && answers[1] === "Â§ß„Åç„ÅÑ") {
      return { en: "retriever/golden", jp: breedMapping["retriever/golden"] }; // „Ç¥„Éº„É´„Éá„É≥„Éª„É¨„Éà„É™„Éê„Éº
    } else {
      return { en: "beagle", jp: breedMapping["beagle"] }; // „Éì„Éº„Ç∞„É´
    }
  };

  const handleAnswer = (answer) => {
    const updatedAnswers = [...answers, answer];
    setAnswers(updatedAnswers);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      const breed = determineBreed(updatedAnswers);
      setSelectedBreed(breed); // Áä¨Á®Æ„Çí„Ç™„Éñ„Ç∏„Çß„ÇØ„Éà„ÅßÊ∏°„Åô
    }
  };

  return (
    <div className="container">
      <h1 className="mt-4">„ÅÇ„ÅÆÁä¨„ÅÆÁ®ÆÈ°û„ÅØÔºüüêï</h1>
      {selectedBreed ? (
        <Result breed={selectedBreed} />
      ) : (
        <Question
          question={questions[currentQuestion].question}
          options={questions[currentQuestion].options}
          onAnswer={handleAnswer}
        />
      )}
    </div>
  );
}

export default App;
