import React, { useState } from 'react';
import Question from './Question';
import Result from './Result';

function App() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [selectedBreed, setSelectedBreed] = useState(null);

  const questions = [
    { question: "ËÄ≥„ÅØÁ´ã„Å£„Å¶„ÅÑ„Åæ„Åô„ÅãÔºü", options: ["„ÅØ„ÅÑ", "„ÅÑ„ÅÑ„Åà"] },
    { question: "„Åó„Å£„ÅΩ„ÅØÈï∑„ÅÑÔºü", options: ["„ÅØ„ÅÑ", "„ÅÑ„ÅÑ„Åà"] },
    { question: "ËÉ¥„ÅØÈï∑„ÅÑÔºü", options: ["„ÅØ„ÅÑ", "„ÅÑ„ÅÑ„Åà"] },
    { question: "Ë∂≥„ÅØÈï∑„ÅÑÔºü", options: ["„ÅØ„ÅÑ", "„ÅÑ„ÅÑ„Åà"] }
  ];

  // Ëã±Ë™û ‚Üí Êó•Êú¨Ë™û„ÅÆÂ§âÊèõË°® & Ë™¨ÊòéÊñá„ÇíËøΩÂä†
  const breedMapping = {
    shiba: { jp: "Êü¥Áä¨", desc: "Êü¥Áä¨„ÅØÊó•Êú¨ÂéüÁî£„ÅÆÁä¨„Åß„ÄÅÂø†Ë™†ÂøÉ„ÅåÂº∑„ÅèÊ¥ªÁô∫„Å™ÊÄßÊ†º„Åß„Åô„ÄÇ" },
    "retriever/golden": { jp: "„Ç¥„Éº„É´„Éá„É≥„Éª„É¨„Éà„É™„Éê„Éº", desc: "„Ç¥„Éº„É´„Éá„É≥„Éª„É¨„Éà„É™„Éê„Éº„ÅØÂèãÂ•ΩÁöÑ„ÅßË≥¢„Åè„ÄÅÂÆ∂Â∫≠Áä¨„Å®„Åó„Å¶‰∫∫Ê∞ó„Åß„Åô„ÄÇ" },
    beagle: { jp: "„Éì„Éº„Ç∞„É´", desc: "„Éì„Éº„Ç∞„É´„ÅØÂóÖË¶ö„ÅåÂÑ™„Çå„ÅüÂ∞èÂûã„ÅÆÁåüÁä¨„Åß„ÄÅÂ•ΩÂ•áÂøÉÊó∫Áõõ„Å™ÊÄßÊ†º„Åß„Åô„ÄÇ" },
    corgi: { jp: "„Ç≥„Éº„ÇÆ„Éº", desc: "„Ç≥„Éº„ÇÆ„Éº„ÅØÁü≠„ÅÑË∂≥„Å®Èï∑„ÅÑËÉ¥„ÅåÁâπÂæ¥„Åß„ÄÅ„Å®„Å¶„ÇÇÊ¥ªÁô∫„Å™Áä¨Á®Æ„Åß„Åô„ÄÇ" },
    husky: { jp: "„Ç∑„Éô„É™„Ç¢„É≥„Éè„Çπ„Ç≠„Éº", desc: "„Éè„Çπ„Ç≠„Éº„ÅØÂØíÂÜ∑Âú∞„Å´ÈÅ©Âøú„Åó„ÅüÁä¨„Åß„ÄÅ„Ç™„Ç™„Ç´„Éü„ÅÆ„Çà„ÅÜ„Å™Ë¶ã„ÅüÁõÆ„ÅåÁâπÂæ¥„Åß„Åô„ÄÇ" },
    poodle: { jp: "„Éó„Éº„Éâ„É´", desc: "„Éó„Éº„Éâ„É´„ÅØÈùûÂ∏∏„Å´Áü•ËÉΩ„ÅåÈ´ò„Åè„ÄÅ„Ç´„ÉÉ„Éà„Çπ„Çø„Ç§„É´„ÇÇË±äÂØå„Å™‰∫∫Ê∞ó„ÅÆÁä¨Á®Æ„Åß„Åô„ÄÇ" },
    dalmatian: { jp: "„ÉÄ„É´„É°„Ç∑„Ç¢„É≥", desc: "„ÉÄ„É´„É°„Ç∑„Ç¢„É≥„ÅØÁôΩÂú∞„Å´Èªí„ÅÑÊñëÁÇπ„Åå„ÅÇ„ÇãÁä¨Á®Æ„Åß„ÄÅÊ¥ªÁô∫„ÅßÈÅä„Å≥Â•Ω„Åç„Åß„Åô„ÄÇ" }
  };

  // ÂõûÁ≠î„ÅÆÁµÑ„ÅøÂêà„Çè„Åõ„ÅßÁä¨Á®Æ„ÇíÁâπÂÆö
  const determineBreed = (answers) => {
    if (answers[0] === "„ÅØ„ÅÑ" && answers[1] === "Â∞è„Åï„ÅÑ") {
      return { en: "shiba", ...breedMapping["shiba"] };
    } else if (answers[0] === "„ÅÑ„ÅÑ„Åà" && answers[1] === "Â§ß„Åç„ÅÑ") {
      return { en: "retriever/golden", ...breedMapping["retriever/golden"] };
    } else {
      return { en: "beagle", ...breedMapping["beagle"] };
    }
  };

  const handleAnswer = (answer) => {
    const updatedAnswers = [...answers, answer];
    setAnswers(updatedAnswers);
    // Ë≥™ÂïèÁµÇ‰∫ÜÂàÜÂ≤êÂàÜÂ≤ê
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      const breed = determineBreed(updatedAnswers);
      setSelectedBreed(breed);
    }
  };

  return (
    <div className="container">
      <h1 className="mt-4 text-primary">„ÅÇ„ÅÆÁä¨„ÅÆÁ®ÆÈ°û„ÅØÔºüüêï</h1>
      {selectedBreed ? (
        <Result breed={selectedBreed} />
      ) : (
        <Question
          question={questions[currentQuestion].question}
          options={questions[currentQuestion].options}
          onAnswer={handleAnswer}
        />
      )}
    </div>
  );
}

export default App;
