import React, { useEffect, useState } from 'react';

function Result({ breed }) {
  const [imageUrl, setImageUrl] = useState("");

  useEffect(() => {
    if (!breed) return;

    const fetchImage = async () => {
      try {
        const response = await fetch(`https://dog.ceo/api/breed/${breed.en}/images/random`);
        const data = await response.json();
        setImageUrl(data.message);
      } catch (error) {
        console.error("画像取得に失敗しました", error);
      }
    };

    fetchImage();
  }, [breed]);

  return (
    <div className="container mt-4">
      <h3 style={{ textDecoration: "underline" }}>▼判定結果</h3>
      <h2>{breed.jp}</h2>
      <p className="mt-2">{breed.desc}</p>
      {imageUrl ? <img src={imageUrl} alt={breed.jp} className="img-fluid mt-3" /> : <p>画像を取得中...</p>}
    </div>
  );
}

export default Result;
