import React, { useState } from 'react';
import Question from './Question';
import Result from './Result';

function App() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [selectedBreed, setSelectedBreed] = useState(null);

  const questions = [
    { question: "耳は立っていますか？", options: ["はい", "いいえ"] },
    { question: "体の大きさは？", options: ["小さい", "中くらい", "大きい"] },
    { question: "毛の長さは？", options: ["短い", "中くらい", "長い"] }
  ];

  // 英語 → 日本語の変換表
  const breedMapping = {
    shiba: "柴犬",
    "retriever/golden": "ゴールデン・レトリバー",
    beagle: "ビーグル"
  };

  // 回答の組み合わせで犬種を特定
  const determineBreed = (answers) => {
    if (answers[0] === "はい" && answers[1] === "小さい") {
      return { en: "shiba", jp: breedMapping["shiba"] }; // 柴犬
    } else if (answers[0] === "いいえ" && answers[1] === "大きい") {
      return { en: "retriever/golden", jp: breedMapping["retriever/golden"] }; // ゴールデン・レトリバー
    } else {
      return { en: "beagle", jp: breedMapping["beagle"] }; // ビーグル
    }
  };

  const handleAnswer = (answer) => {
    const updatedAnswers = [...answers, answer];
    setAnswers(updatedAnswers);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      const breed = determineBreed(updatedAnswers);
      setSelectedBreed(breed); // 犬種をオブジェクトで渡す
    }
  };

  return (
    <div className="container">
      <h1 className="mt-4">犬種判定</h1>
      {selectedBreed ? (
        <Result breed={selectedBreed} />
      ) : (
        <Question
          question={questions[currentQuestion].question}
          options={questions[currentQuestion].options}
          onAnswer={handleAnswer}
        />
      )}
    </div>
  );
}

export default App;
